import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const Cart = ({ isOpen, onClose, token }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchCartItems = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await axios.get('/carts', {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.data.length > 0) {
        setCartItems(response.data[0].items || []);
      } else {
        setCartItems([]);
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
      setError('Failed to load cart items');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    if (isOpen) {
      fetchCartItems();
    }
  }, [isOpen, token, fetchCartItems]);

  const removeFromCart = async (itemId) => {
    try {
      await axios.delete(`/carts/items/${itemId}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      fetchCartItems(); // Refresh cart
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      const quantity = item.quantity || 1;
      const price = item.item?.price || 0;
      return total + (price * quantity);
    }, 0);
  };

  if (!isOpen) return null;

  return (
    <div className="cart-overlay" onClick={onClose}>
      <div className="cart-modal" onClick={(e) => e.stopPropagation()}>
        <div className="cart-header">
          <h2>🛒 Your Cart</h2>
          <button className="cart-close-btn" onClick={onClose}>×</button>
        </div>

        <div className="cart-content">
          {loading ? (
            <div className="cart-loading">
              <div className="loading-spinner"></div>
              <p>Loading your cart...</p>
            </div>
          ) : error ? (
            <div className="cart-error">
              <p>❌ {error}</p>
            </div>
          ) : cartItems.length === 0 ? (
            <div className="cart-empty">
              <div className="empty-cart-icon">🛍️</div>
              <h3>Your cart is empty</h3>
              <p>Add some amazing products to get started!</p>
            </div>
          ) : (
            <>
              <div className="cart-items">
                {cartItems.map((cartItem) => (
                                     <div key={cartItem.id} className="cart-item">
                     <div className="cart-item-image">
                       <img 
                         src={`https://source.unsplash.com/72x72/?${encodeURIComponent(cartItem.item.name)}`}
                         alt={cartItem.item.name}
                         style={{
                           width: '100%',
                           height: '100%',
                           objectFit: 'cover',
                           borderRadius: '16px'
                         }}
                         onError={(e) => {
                           e.target.style.display = 'none';
                           e.target.nextSibling.style.display = 'flex';
                         }}
                       />
                       <div style={{
                         width: '100%',
                         height: '100%',
                         background: 'linear-gradient(135deg, #667eea, #764ba2)',
                         display: 'none',
                         alignItems: 'center',
                         justifyContent: 'center',
                         fontSize: '1.8rem',
                         color: 'white',
                         fontWeight: '700',
                         borderRadius: '16px',
                         position: 'absolute',
                         top: 0,
                         left: 0
                       }}>
                         {cartItem.item.name.charAt(0)}
                       </div>
                     </div>
                    <div className="cart-item-details">
                      <h4>{cartItem.item.name}</h4>
                      <p className="cart-item-category">{cartItem.item.category}</p>
                      <p className="cart-item-price">${cartItem.item.price.toFixed(2)}</p>
                    </div>
                    <div className="cart-item-actions">
                      <span className="cart-item-quantity">Qty: {cartItem.quantity || 1}</span>
                      <button 
                        className="remove-item-btn"
                        onClick={() => removeFromCart(cartItem.item_id)}
                      >
                        🗑️
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              
              <div className="cart-summary">
                <div className="cart-total">
                  <span>Total:</span>
                  <span className="total-amount">${calculateTotal().toFixed(2)}</span>
                </div>
                <button className="checkout-btn">
                  💳 Proceed to Checkout
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cart;
