import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ItemList = ({ onAddToCart }) => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState([]);
  const [itemImages, setItemImages] = useState({});

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/items');
      setItems(response.data);

      // Extract unique categories
      const uniqueCategories = [...new Set(response.data.map(item => item.category))];
      setCategories(['All', ...uniqueCategories]);

      // Fetch images for each item
      await fetchItemImages(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
      setError('Failed to load items');
    } finally {
      setLoading(false);
    }
  };

  const fetchItemImages = async (itemsList) => {
    const images = {};

    for (const item of itemsList) {
      try {
        // Create search query based on item name and category
        const searchQuery = `${item.name} ${item.category}`.toLowerCase();

        // Use Unsplash API to get a relevant image
        const imageResponse = await axios.get(
          `https://api.unsplash.com/search/photos?query=${encodeURIComponent(searchQuery)}&per_page=1&orientation=landscape`,
          {
            headers: {
              'Authorization': 'Client-ID YOUR_UNSPLASH_ACCESS_KEY'
            }
          }
        );

        if (imageResponse.data.results && imageResponse.data.results.length > 0) {
          images[item.id] = imageResponse.data.results[0].urls.regular;
        } else {
          // Fallback to a generic product image
          images[item.id] = `https://source.unsplash.com/400x300/?${encodeURIComponent(item.category)}`;
        }
      } catch (error) {
        console.error(`Error fetching image for ${item.name}:`, error);
        // Fallback to a generic product image
        images[item.id] = `https://source.unsplash.com/400x300/?${encodeURIComponent(item.category)}`;
      }
    }

    setItemImages(images);
  };

  const filteredItems = selectedCategory === 'All' 
    ? items 
    : items.filter(item => item.category === selectedCategory);

  const getCategoryIcon = (category) => {
    const icons = {
      'All': '🛍️',
      'Electronics': '💻',
      'Clothing': '👕',
      'Home & Garden': '🏠',
      'Books': '📚',
      'Sports': '⚽'
    };
    return icons[category] || '🛍️';
  };

  const getCategoryColor = (category) => {
    const colors = {
      'All': 'linear-gradient(135deg, #667eea, #764ba2)',
      'Electronics': 'linear-gradient(135deg, #667eea, #764ba2)',
      'Clothing': 'linear-gradient(135deg, #ed8936, #dd6b20)',
      'Home & Garden': 'linear-gradient(135deg, #48bb78, #38a169)',
      'Books': 'linear-gradient(135deg, #9f7aea, #805ad5)',
      'Sports': 'linear-gradient(135deg, #f56565, #e53e3e)'
    };
    return colors[category] || 'linear-gradient(135deg, #667eea, #764ba2)';
  };

  if (loading) {
    return (
      <div className="loading">
        <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>🔄</div>
        Loading amazing products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="error">
        <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>❌</div>
        {error}
      </div>
    );
  }

  return (
    <div>
      {/* Categories Section */}
      <div className="categories-section">
        <h2 className="categories-title">Shop by Category</h2>
        <div className="categories-grid">
          {categories.map(category => (
            <div
              key={category}
              className="category-card"
              onClick={() => setSelectedCategory(category)}
              style={{
                border: selectedCategory === category ? '3px solid #667eea' : '1px solid rgba(255, 255, 255, 0.2)',
                transform: selectedCategory === category ? 'translateY(-5px)' : 'translateY(0)'
              }}
            >
              <div className="category-title">
                <div 
                  className="category-icon"
                  style={{ background: getCategoryColor(category) }}
                >
                  {getCategoryIcon(category)}
                </div>
                {category}
              </div>
              <div style={{ color: '#666', fontSize: '0.9rem' }}>
                {category === 'All' 
                  ? `${items.length} items` 
                  : `${items.filter(item => item.category === category).length} items`
                }
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Items Section */}
      <div>
        <h2 className="categories-title">
          {selectedCategory === 'All' ? 'All Products' : `${selectedCategory} Products`} ({filteredItems.length} items)
        </h2>
        <div className="items-grid">
          {filteredItems.map(item => (
            <div key={item.id} className="item-card">
              <div className="item-image">
                {itemImages[item.id] ? (
                  <img 
                    src={itemImages[item.id]} 
                    alt={item.name}
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      borderRadius: '20px 20px 0 0'
                    }}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : (
                  <div style={{
                    width: '100%',
                    height: '100%',
                    background: 'linear-gradient(135deg, #667eea, #764ba2)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '4rem',
                    color: 'white',
                    fontWeight: '700'
                  }}>
                    {item.name.charAt(0)}
                  </div>
                )}
                {/* Fallback placeholder */}
                <div style={{
                  width: '100%',
                  height: '100%',
                  background: 'linear-gradient(135deg, #667eea, #764ba2)',
                  display: itemImages[item.id] ? 'none' : 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '4rem',
                  color: 'white',
                  fontWeight: '700',
                  position: 'absolute',
                  top: 0,
                  left: 0
                }}>
                  {item.name.charAt(0)}
                </div>
              </div>
              <div className="item-details">
                <div className="item-category">{item.category}</div>
                <div className="item-name">{item.name}</div>
                <div className="item-description">{item.description}</div>
                <div className="item-price">${item.price.toFixed(2)}</div>
                <button
                  className="add-to-cart-btn"
                  onClick={() => onAddToCart(item.id)}
                >
                  🛒 Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>

        {filteredItems.length === 0 && (
          <div style={{ 
            textAlign: 'center', 
            color: 'white', 
            fontSize: '1.2rem',
            padding: '40px'
          }}>
            No items found in this category.
          </div>
        )}
      </div>
    </div>
  );
};

export default ItemList;
