import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Login from './components/Login';
import ItemList from './components/ItemList';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const savedToken = localStorage.getItem('token');
    if (savedToken) {
      setToken(savedToken);
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = async (username, password) => {
    try {
      console.log('Attempting login with:', username, password);
      const response = await axios.post('http://localhost:8080/api/users/login', {
        username,
        password
      });
      
      console.log('Login response:', response.data);
      
      if (response.data.token) {
        setToken(response.data.token);
        setIsLoggedIn(true);
        setUser(response.data.user);
        localStorage.setItem('token', response.data.token);
        toast.success('Login successful! Welcome to our store!');
      }
    } catch (error) {
      console.error('Login error:', error);
      toast.error('Invalid username or password');
    }
  };

  const handleLogout = () => {
    setToken('');
    setIsLoggedIn(false);
    setUser(null);
    localStorage.removeItem('token');
    toast.info('Logged out successfully');
  };

  const handleAddToCart = async (itemId) => {
    if (!token) {
      toast.error('Please login first');
      return;
    }

    try {
      console.log('Adding item to cart:', itemId, 'with token:', token);
      const response = await axios.post('http://localhost:8080/api/carts', {
        item_id: itemId,
        quantity: 1
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      console.log('Add to cart response:', response.data);
      toast.success('Item added to cart successfully!');
    } catch (error) {
      console.error('Add to cart error:', error);
      toast.error('Failed to add item to cart');
    }
  };

  const handleCheckout = async () => {
    if (!token) {
      toast.error('Please login first');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/orders', {}, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      toast.success('Order placed successfully! Thank you for your purchase!');
    } catch (error) {
      console.error('Checkout error:', error);
      toast.error('Failed to place order');
    }
  };

  const handleViewCart = async () => {
    if (!token) {
      toast.error('Please login first');
      return;
    }

    try {
      const response = await axios.get('http://localhost:8080/api/carts', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      const cartItems = response.data;
      if (cartItems.length === 0) {
        toast.info('Your cart is empty');
      } else {
        const itemDetails = cartItems.map(item => 
          `Item ID: ${item.item_id}, Quantity: ${item.quantity}`
        ).join('\n');
        toast.info(`Cart Items:\n${itemDetails}`);
      }
    } catch (error) {
      console.error('View cart error:', error);
      toast.error('Failed to load cart');
    }
  };

  const handleViewOrders = async () => {
    if (!token) {
      toast.error('Please login first');
      return;
    }

    try {
      const response = await axios.get('http://localhost:8080/api/orders', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      const orders = response.data;
      if (orders.length === 0) {
        toast.info('No orders found');
      } else {
        const orderDetails = orders.map(order => 
          `Order ID: ${order.id}, Total: $${order.total}`
        ).join('\n');
        toast.info(`Order History:\n${orderDetails}`);
      }
    } catch (error) {
      console.error('View orders error:', error);
      toast.error('Failed to load orders');
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="App">
        <div className="login-container">
          <Login onLogin={handleLogin} />
        </div>
        <ToastContainer />
      </div>
    );
  }

  return (
    <div className="App">
      <header className="header">
        <div className="header-content">
          <a href="#" className="logo">🛍️ ShopHub</a>
          <div className="nav-buttons">
            <button className="btn btn-secondary" onClick={handleViewCart}>
              🛒 Cart
            </button>
            <button className="btn btn-warning" onClick={handleViewOrders}>
              📋 Orders
            </button>
            <button className="btn btn-success" onClick={handleCheckout}>
              💳 Checkout
            </button>
            <button className="btn btn-secondary" onClick={handleLogout}>
              👋 Logout
            </button>
          </div>
        </div>
      </header>

      <div className="container">
        <ItemList 
          onAddToCart={handleAddToCart}
          token={token}
        />
      </div>

      <ToastContainer />
    </div>
  );
}

export default App;
