import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ItemList = ({ onAddToCart }) => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/items');
      setItems(response.data);
      
      // Extract unique categories
      const uniqueCategories = [...new Set(response.data.map(item => item.category))];
      setCategories(['All', ...uniqueCategories]);
    } catch (error) {
      console.error('Error fetching items:', error);
      setError('Failed to load items');
    } finally {
      setLoading(false);
    }
  };

  const filteredItems = selectedCategory === 'All' 
    ? items 
    : items.filter(item => item.category === selectedCategory);

  const getCategoryIcon = (category) => {
    const icons = {
      'Electronics': '💻',
      'Clothing': '👕',
      'Home & Garden': '🏠',
      'Books': '📚',
      'Sports': '⚽'
    };
    return icons[category] || '🛍️';
  };

  const getCategoryColor = (category) => {
    const colors = {
      'Electronics': 'linear-gradient(135deg, #667eea, #764ba2)',
      'Clothing': 'linear-gradient(135deg, #f093fb, #f5576c)',
      'Home & Garden': 'linear-gradient(135deg, #4facfe, #00f2fe)',
      'Books': 'linear-gradient(135deg, #43e97b, #38f9d7)',
      'Sports': 'linear-gradient(135deg, #fa709a, #fee140)'
    };
    return colors[category] || 'linear-gradient(135deg, #667eea, #764ba2)';
  };

  if (loading) {
    return (
      <div className="loading">
        <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>🔄</div>
        Loading amazing products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="error">
        <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>❌</div>
        {error}
      </div>
    );
  }

  return (
    <div>
      {/* Categories Section */}
      <div className="categories-section">
        <h2 className="categories-title">Shop by Category</h2>
        <div className="categories-grid">
          {categories.map(category => (
            <div
              key={category}
              className="category-card"
              onClick={() => setSelectedCategory(category)}
              style={{
                border: selectedCategory === category ? '3px solid #667eea' : '1px solid rgba(255, 255, 255, 0.2)',
                transform: selectedCategory === category ? 'translateY(-5px)' : 'translateY(0)'
              }}
            >
              <div className="category-title">
                <div 
                  className="category-icon"
                  style={{ background: getCategoryColor(category) }}
                >
                  {getCategoryIcon(category)}
                </div>
                {category}
              </div>
              <div style={{ color: '#666', fontSize: '0.9rem' }}>
                {category === 'All' 
                  ? `${items.length} items` 
                  : `${items.filter(item => item.category === category).length} items`
                }
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Items Section */}
      <div>
        <h2 className="categories-title">
          {selectedCategory === 'All' ? 'All Products' : `${selectedCategory} Products`} ({filteredItems.length} items)
        </h2>
        <div className="items-grid">
          {filteredItems.map(item => (
            <div key={item.id} className="item-card">
              <div className="item-image">
                {item.name.charAt(0)}
              </div>
              <div className="item-details">
                <div className="item-category">{item.category}</div>
                <div className="item-name">{item.name}</div>
                <div className="item-description">{item.description}</div>
                <div className="item-price">${item.price.toFixed(2)}</div>
                <button
                  className="add-to-cart-btn"
                  onClick={() => onAddToCart(item.id)}
                >
                  🛒 Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
        
        {filteredItems.length === 0 && (
          <div style={{ 
            textAlign: 'center', 
            color: 'white', 
            fontSize: '1.2rem',
            padding: '40px'
          }}>
            No items found in this category.
          </div>
        )}
      </div>
    </div>
  );
};

export default ItemList;
